package day02proj01;

public class Test01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     System.out.println("Arithmetic Operater Demo ");
     //Variables
     int a=5,b=2;
     System.out.println("a+b= " +(a+b));
     System.out.println("a-b= " +(a-b));
     System.out.println("a+*b= " +(a*b));
     //Quotient
     System.out.println("a/b= " +(a/b));
     //reminder
     System.out.println("a%b= " +(a%b));
     System.out.println();
	}

}
